function renderPV(t,e,n,i,s){fetch("https://www.bigink.cn/api/shanmu-pv-statistics/"+t).then(t=>t.json()).then(t=>e.innerText=`${i}${t.data.pageView+n}${s}`)}function renderUV(t,e,n,i,s){const a=`bigink-count_${t}`,r=sessionStorage.getItem(a);r?e.innerText=`${i}${+r+n}${s}`:fetch("https://www.bigink.cn/api/shanmu-uv-statistics/"+t).then(t=>t.json()).then(t=>{e.innerText=`${i}${t.data.uniqueView+n}${s}`,sessionStorage.setItem(a,`${t.data.uniqueView}`)})}document.addEventListener("readystatechange",()=>{"complete"===document.readyState&&[...document.getElementsByClassName("bigink-count")].forEach(t=>{const e=t.getAttribute("pv_id"),n=t.getAttribute("uv_id"),i=t.getAttribute("prefix")||"",s=t.getAttribute("suffix")||"",a=t.getAttribute("base-count")||0;e&&renderPV(e,t,+a,i,s),n&&renderUV(n,t,+a,i,s)})});